mapper.update:
update member set
	MEM_EMAIL = #{memEmail},
	MEM_PHONE = #{memPhone}
	
		MEM_NUM = #{memNum}

merge into wish w using (select mem_num from member) m on (w.mem_num = m.mem_num)
when matched then update set wish_date = SYSDATE delete where mem_num = #{userNum} and store_num = #{storeNum}
when not matched then insert (MEM_NUM, STORE_NUM, WISH_DATE) values(#{userNum}, #{storeNum}, SYSDATE)

update member
set mem_pw = #{_newPw}
where mem_id = #{_memId}
